#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <pthread.h>
#include <unistd.h>
#include <semaphore.h>

sem_t semaphore;

void threadfunc() {
    int i = 0;
    for (; i<10; i++) {
        // 
        sem_wait(&semaphore);
        printf("Hello from da thread!\n");
        sem_post(&semaphore);
        sleep(1);
    }
}

int main(void) {
    // 
    sem_init(&semaphore, 0, 1);

    pthread_t *mythread;    
    mythread = (pthread_t *)malloc(sizeof(*mythread));

    // 
    printf("Starting thread, semaphore is unlocked.\n");
    pthread_create(mythread, NULL, (void*)threadfunc, NULL);
    pthread_join(mythread, NULL);

    return 0;
}
